import Key from "./Key";

class Products extends React.Component {
  constructor(props) {
    super(props);
 

    this.state = {
      error: null,
      isLoaded: false,

      bodyGetFields: {
        action: "get_fields",
        params: {field: "brand", offset: 3, limit: 5}
      },
      bodyFilter: {
        action: "filter",
        params: {}
      }

    };
  }
  ids = []
  items = []
  body = {
    action: "get_ids",
    params: {offset: 0, limit: 50}
  }
  

  async makeRequest(api, apiBody) {
      let response = await fetch(api,
      { method: 'POST',
        headers: {
          'Content-Type': 'application/json;charset=utf-8',
          'X-Auth': Key()
        },
        body: JSON.stringify(apiBody)
      })
      let result = await response.json()
      console.log(result)
  }

  takeData() {
    this.makeRequest("https://api.valantis.store:41000/", this.body)

    
  }
  

  componentDidMount() {
    this.takeData()
  }
  componentWillUnmount() { 
    
   }

  render() {
    
    const { error, isLoaded } = this.state;
    if (error) {
      return <div>Ошибка: {error.message}</div>;
