import React from "react";
import Key from "./Key";

class Products extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      error: null,
      isLoaded: false,
      isLoaded2: false,
      items: [],
      bodyGetIds: {
        action: "get_ids",
        params: {offset: 0, limit: 50}
      },
      bodyGetItems: {
        action: "get_items",
        params: {ids: []}
      },
      bodyGetFields: {
        action: "get_fields",
        params: {field: "brand", offset: 3, limit: 5}
      },
      bodyFilter: {
        action: "filter",
        params: {}
      }

    };
  }

  async makeRequest(api, apiBody) {
    fetch(api,
    { method: 'POST',
      headers: {
        'Content-Type': 'application/json;charset=utf-8',
        'X-Auth': Key()
      },
      body: JSON.stringify(apiBody)
    }
    )
      .then(res => res.json())
      .then(
        (res) => {
          switch (apiBody.action) {
            case 'get_ids':
              let tempSet = new Set(res.result)
              this.setState({
                isLoaded: true,
                bodyGetItems: {
                  action: "get_items",
                  params: {ids: Array.from(tempSet)}
                }
              }); 
              break;
            case 'get_items':
              this.setState({
                isLoaded: true,
                items: res.result
              }); 
              break;          
            default:
              break;
          }
        },
        (error) => {
          
          this.setState({
            isLoaded: true,
            error
          });
          this.makeRequest(api, apiBody)
        }
      )
  }

  takeData() {
    this.makeRequest("https://api.valantis.store:41000/", this.state.bodyGetIds)
      .then(this.makeRequest("https://api.valantis.store:41000/", this.state.bodyGetItems))
    
  }
  componentDidMount() {
    this.takeData()
  }

  render() {
    const { error, isLoaded, items } = this.state;
    if (error) {
      return <div>Ошибка: {error.message}</div>;
    } else if (!isLoaded) {
      return <div>Загрузка...</div>;
    } else {
