        <div className="">
{/*           {items.map(item => (
            <div key={item.id}>
              <p>
                Бренд: {item.brand}
                id товара: {item.id}
                Цена: {item.price}
                Название: {item.product}
                </p>
            </div>
          ))} */}
        </div>
        
      );    }
  }
}

export default Products
