import React from "react";
import Key from "./Key";

class Products extends React.Component {
  constructor(props) {
    super(props);
 
    this.state = {
      error: null,
      isLoaded: false,
      ids: [],
      bodyGetFields: {
        action: "get_fields",
        params: {field: "brand", offset: 3, limit: 5}
      },
      bodyFilter: {
        action: "filter",
        params: {}
      }
    };
  }
  items = []
  body = {
    action: "get_ids",
    params: {offset: 0, limit: 50}
  }
  

  async makeRequest(api, apiBody) {
      fetch(api,
      { method: 'POST',
        headers: {
          'Content-Type': 'application/json;charset=utf-8',
          'X-Auth': Key()
        },
        body: JSON.stringify(apiBody)
      })
        .then(res => res.json())
        .then(res => {
          let tempSet = new Set(res.result)
          let result = Array.from(tempSet)
          return result
        })
        .then(res => this.setState({
          isLoaded: true,
          ids: res
        }))
        .then(console.log(this.ids))

  }

  async takeData() {
    this.makeRequest("https://api.valantis.store:41000/", this.body)

/*     this.items = await this.makeRequest("https://api.valantis.store:41000/", {
      action: "get_items",
      params: {ids: this.ids}
    })
    console.log(this.ids) */
    
  }
  

  componentDidMount() {
    this.takeData()
  }
  componentWillUnmount() { 
    
   }

  render() {
    
    const { error, isLoaded, ids} = this.state;
    if (error) {
      return <div>Ошибка: {error.message}</div>;
    } else if (!isLoaded) {
      return <div>Загрузка...</div>;
    } else {
      this.makeRequest("https://api.valantis.store:41000/", {
        action: "get_items",
        params: {ids: this.state.ids}
      })
      return (
        <div className="1" key={2}>

          {ids.map(item => (
            
            <div key={item}>
              {item}
            </div>
          ))}
        </div>
        
      );    }
  }
}

export default Products
