import React from "react";
import Key from "./Key";

class Products extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      error: null,
      isLoaded: false,
      isLoaded2: false,
      items: [],
      bodyGetIds: {
        action: "get_ids",
        params: {offset: 0, limit: 50}
      },
      bodyGetItems: {
        action: "get_items",
        params: {ids: []}
      },
      bodyGetFields: {
        action: "get_fields",
        params: {field: "brand", offset: 3, limit: 5}
      },
      bodyGetIds: {
        action: "filter",
        params: {}
      }

    };
  }

  makeRequest(api, apiBody) {
    fetch(api,
    { method: 'POST',
      headers: {
        'Content-Type': 'application/json;charset=utf-8',
        'X-Auth': Key()
      },
      body: JSON.stringify(apiBody)
    }
    )
      .then(res => res.json())
      .then(
        (res) => {
          
          let tempSet = new Set(res.result)
          
          this.setState({
            isLoaded: true
          }); 
        },
        (error) => {
          
          this.setState({
            isLoaded: true,
            error
          });
        }
      )
  }

  componentDidMount() {
    this.makeRequest("https://api.valantis.store:41000/", this.state.bodyGetIds)
    console.log(this.state.bodyGetIds)
  }

  render() {
    const { error, isLoaded, items } = this.state;
    if (error) {
      return <div>Ошибка: {error.message}</div>;
    } else if (!isLoaded) {
      return <div>Загрузка...</div>;
    } else {
      console.log(items)
      return (
        <div className="">
{/*           {items.map(item => (
            <div key={item.id}>
              <p>
                Бренд: {item.brand}
                id товара: {item.id}
                Цена: {item.price}
                Название: {item.product}
                </p>
            </div>
          ))} */}
        </div>
        
      );    }
  }
}

export default Products
