import React from "react"
import Key from "./Key";
import ServerError from "./ServerError";
import AuthorizationError from "./AuthorizationError";
import filterDuplicates from "./FilterDuplicates";

function Request(action, params) {


  const body = {
    action: action, 
    params: params
  }
  const response = fetch('https://api.valantis.store:41000/',
    { method: 'POST',
    headers: {
      'Content-Type': 'application/json;charset=utf-8',
      'X-Auth': Key()
    },
    body: JSON.stringify(body)
    }
  ).then((res) => { //обрабатываем ответ сервера (не ошибка для then)
    if(!res.ok) {
      switch (res.status) {
        case 500:
          console.log(`Ошибка запроса id товаров. Код: ${res.status}`)
          throw new ServerError
          break;
        case 401:
          console.log(`Ошибка запроса id товаров. Код: ${res.status}`)
          throw new AuthorizationError
          break;
      
        default:
          break;
      }
    } else {
      return res
    }
  }, (e) => { throw e }).then((res) => { //приводим строку ответа к объекту 
    return res.json() 
  }, (e) => { throw e; }).then((res) => { // фильтруем дубликаты
    if (body.action == "get_ids") {
      return filterDuplicates(res.result)
    } else {
      return res
    }    
  }, (e) => { throw e; })


  return response
}


export default Request
