import React from "react"

class Input extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
      name: null, 
      price: null, 
      brand: null
    }
  }
  changeState = (event) => {
    console.log(event.target.value)
    switch (event.target.className) {
      case "input-name":
        if (event.target.value > 0) {
          this.setState({
            name: event.target.value
          }, () => {
            this.handleChange()
          })
        } else {
          this.setState({
            name: null
          }, () => {
            this.handleChange()
          })
        }
        break;
      case "input-price":
        this.setState({
          price: +event.target.value
        }, () => {
          this.handleChange()
        })
        break;
      case "input-brand":
        this.setState({
          brand: event.target.value
        }, () => {
          this.handleChange()
        })
        break;
    
      default:
        break;
    }
  }
  async handleChange() {
    this.props.changeParentValue({name: this.state.name, price: this.state.price, brand: this.state.brand, page: 0, isLoaded: false})
  }



  render() {
    return (
      <div className="input-container">
        <input className="input-name" type="text" placeholder="Название" onChange={this.changeState}/>
        <input className="input-price" type="number" placeholder="Цена" onChange={this.changeState} />
        <select className="input-brand" placeholder="Бренд" onChange={this.changeState}/>
          <option value={null}>Не выбрано</option>
          <option value="Piaget">Piaget</option>
          <option value="Cartier">Cartier</option>
        <select/>
      </div>
    )
  }
}

export default Input
