import React from "react"
import NavigationButton from "./NavigationButton"

class Navigation extends React.Component {
  constructor(props) {
    super(props)

    this.state = {
      page: 0
    }

    this.handleChange = this.handleChange.bind(this)
    this.changePageValue = this.changePageValue.bind(this)
  }
  handleChange(value) {
    this.props.changePageValue(value)
  }
  changePageValue(value) {
    this.handleChange(value)
  }



  render() {
    return (
    <nav className="navigation-container" >
      <NavigationButton key={0} page={0} changePageValue = {this.changePageValue} />
      <NavigationButton key={1} page={1} changePageValue = {this.changePageValue}/>
      <NavigationButton key={2} page={2} changePageValue = {this.changePageValue}/>
      <NavigationButton key={3} page={3} changePageValue = {this.changePageValue}/>
    </nav>
    )
  }
}

export default Navigation
