import React from "react";
import Key from "./Key";

class Card extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      error: null,
      isLoaded: false,
      ids: [],
      body: {
        action: "get_ids",
        params: {offset: 0, limit: 10}
      }
    };
  }

  componentDidMount() {
    fetch("https://api.valantis.store:41000/",
    { method: 'POST',
      headers: {
        'Content-Type': 'application/json;charset=utf-8',
        'X-Auth': Key()
      },
      body: JSON.stringify(this.state.body)
    }
    )
      .then(res => res.json()
      )
      .then(
        (res) => {
          
          let tempSet = new Set(res.result)
          
          this.setState({
            isLoaded: true,
            ids: Array.from(tempSet)
          });
        },
        // Примечание: важно обрабатывать ошибки именно здесь, а не в блоке catch(),
        // чтобы не перехватывать исключения из ошибок в самих компонентах.
        (error) => {
          
          this.setState({
            isLoaded: true,
            error
          });
        }
      )
  }

  render() {
    const { error, isLoaded, ids } = this.state;
    if (error) {
      return <div>Ошибка: {error.message}</div>;
    } else if (!isLoaded) {
      return <div>Загрузка...</div>;
    } else {
      return (
        <ul>
          {ids.map(id => (
            <li key={id}>
              {id}
            </li>
          ))}
        </ul>
      );    }
  }
}

export default Card
