import React from "react";
import Key from "./Key";

class Products extends React.Component {
  constructor(props) {
    super(props);
 
    this.state = {
      error: null,
      isLoaded: false,
      items: [],
      bodyGetFields: {
        action: "get_fields",
        params: {field: "brand", offset: 3, limit: 5}
      },
      bodyFilter: {
        action: "filter",
        params: {}
      }
    };
  }

  

  async makeRequest(api) {

      let apiBody = {
        action: "get_ids",
        params: {offset: 0, limit: 50}
      }
      fetch(api,
      { method: 'POST',
        headers: {
          'Content-Type': 'application/json;charset=utf-8',
          'X-Auth': Key()
        },
        body: JSON.stringify(apiBody)
      })
        .then(res => res.json())
        .then(res => {
          let tempSet = new Set(res.result)
          let result = Array.from(tempSet)
          return result
        })
        .then(res => {
          let apiBodyItems = {
              action: "get_items",
              params: {ids: res}
          };
          fetch(api,
            { method: 'POST',
              headers: {
                'Content-Type': 'application/json;charset=utf-8',
                'X-Auth': Key()
              },
              body: JSON.stringify(apiBody)
            })
            .then(res => res.json())
            .then(res => this.setState({
              isLoaded: true,
              items: res
            }))
          })

  }

  async takeData() {
    this.makeRequest("https://api.valantis.store:41000/")

/*     this.items = await this.makeRequest("https://api.valantis.store:41000/", {
      action: "get_items",
      params: {ids: this.ids}
    })
    console.log(this.ids) */
    
  }
  

  componentDidMount() {
    this.takeData()
  }
  componentWillUnmount() { 
    
   }

  render() {
    
    const { error, isLoaded, items} = this.state;
    if (error) {
      return <div>Ошибка: {error.message}</div>;
    } else if (!isLoaded) {
      return <div>Загрузка...</div>;
    } else {
      return (
        <div className="1" key={2}>

          {items.map(item => (
            
            <div key={item.id}>
              {item.name}
            </div>
          ))}
        </div>
        
      );    }
  }
}

export default Products
