import React from "react";
import Key from "../Key";
import ServerError from "../ServerError";
import AuthorizationError from "../AuthorizationError";
import Product from "./Product";
import Request from "../Request";
import filterDuplicates from "../FilterDuplicates";

class Products extends React.Component {
  constructor(props) {
    super(props);
 
    this.state = {
      error: null,
      ids: [],
      items: [],
      displayedIds: [],
      allUnfilteredProductsQuantity: 0,
      limit: 50,
      offset: 0
    };

    this.inputClick = this.inputClick.bind(this)    
    this.takeData = this.takeData.bind(this)
    this.takeProducts = this.takeProducts.bind(this)
    this.takeIds = this.takeIds.bind(this)
    this.updateData = this.updateData.bind(this)

  }
  cashItems = []
  cashIds = []
  beenFiltered = false
  additive = 0
  changable = false
  filterFromCash(data) {
    if (this.cashItems.length > 0) {
      data.map((dataId) => {
        this.cashItems.map((page) => {
          page.map((product) => {
            if (product.id == dataId) {
              data.splice(data.indexOf(dataId), 1)
            }
          })
        })
      })
    } 
    return data
  }

  takeIds(offset, limit, request) {
    if (this.cashItems[this.props.page]) {
      const temp = []
      this.cashItems[this.props.page].forEach((item) => {
        temp.push(item.id)
      })
      return temp
    } else {
      return Request('get_ids', {offset: offset, limit: limit})
        .then((res) => {
          res = this.filterFromCash(res)
          return res
        }, (e) => {
          console.log("ошибка в get_ids")
          return this.takeIds(offset, limit, request)
        })
    }
  }
  takeFilteredIds(body) {
      return Request('filter', body)
        .then((res) => {
          return res
        }, (e) => {
          console.log("ошибка в filter")
          return this.takeFilteredIds(body)
        })

  }
  takeFields(offset, limit, request) {
      let body = {
        field: 'brand',
        offset: offset,
        limit: limit
      }
      return Request('get_fields', body)
        .then((res) => {
          return res
        }, (e) => {
          console.log("ошибка в get_fields")
          return this.takeFields(offset, limit, request)
        })

  }

  async filterByBrand() {

    let brand = await this.takeFilteredIds({brand: this.state.brand} )

    return brand

  }
  async filterByPrice() {
    let price = await this.takeFilteredIds({price: this.state.price} )

    return price
  }
  async filterByName() {
    let name = await this.takeFilteredIds({product: this.state.name} )

    return name
  }


  takeProducts() {
    if (this.cashItems[this.props.page]) {
      this.setState({
        items: this.cashItems[this.props.page]
      }, () => {
        this.handleChange(this.props.page, true) 
      })
    } else if (this.cashIds[this.props.page]) {
      Request('get_items', {ids: this.cashIds[this.props.page]})
      .then((res) => {
        this.setState({
          items: res
        }, () => {
          this.handleChange(this.props.page, true) 
        })
      }, (e) => {
        console.log("ошибка в get_items")
        this.takeProducts()
      })
    } else {
      Request('get_items', {ids: this.state.ids})
        .then((res) => {
          this.setState({
            items: res
          }, () => {

            this.cashItems[this.props.page] = []
            this.cashItems[this.props.page] = this.cashItems[this.props.page].concat(res)


            this.handleChange(this.props.page, true) 
          })
      }, (e) => {
        console.log("ошибка в get_items")
        this.takeProducts()
      })
    }        
  }
  async handleChange(page, isLoaded) {
    this.props.changeParentValue({page: page, isLoaded: isLoaded, allUnfilteredProductsQuantity: this.state.allUnfilteredProductsQuantity })
  }
  completeFilterCash(pool) {
    let tempPool = []
    for (let i = 0;i < Math.trunc(pool.length / 50) + 1; i++) {
      let tempPool2 = []
      for (let j = 0; j < 50 ;j++) {
        if (pool[i * 50 + j]) {
          tempPool2.push(pool[i * 50 + j])
        }
        
      } 

      tempPool.push(tempPool2)
    }
    this.cashIds = tempPool
  }
  filterOutCashIds(pool) {
    let tempArray = []

    this.cashIds.forEach((page) => {
      page.forEach((cashId) => {
        pool.forEach((id) => {
          if (id == cashId) {
            tempArray.push(id)
          }
        })
  
      })
    })
    if (tempArray.length > 50) {
      this.completeFilterCash(tempArray)
    } else {
      this.cashIds = [tempArray]

    }



  }
  async test() {
    if (this.state.brand) {
      let pool = await this.filterByBrand()

      pool = filterDuplicates(pool)
      
      if (pool.length > 50) {
        this.completeFilterCash(pool)
      } else {
        this.cashIds[0] = pool
      }

    } 
    if (this.state.price) {
      let pool = await this.filterByPrice()

      pool = filterDuplicates(pool)
      if (!this.cashIds.length) {
        if (pool.length > 50) {
          this.completeFilterCash(pool)
        } else {
          this.cashIds[0] = pool
        }
      } else {
        this.filterOutCashIds(pool)
      }
    } 
    if (this.state.name) {
      let pool = await this.filterByName()

      pool = filterDuplicates(pool)
      if (!this.cashIds.length) {
        if (pool.length > 50) {
          this.completeFilterCash(pool)
        } else {
          this.cashIds[0] = pool
        }
      } else {
        this.filterOutCashIds(pool)
      }

    } 
    if (!this.cashIds.length) {
      let pool = await this.takeIds(this.state.offset + this.additive, this.state.limit, "takeIds")
      let allUnfilteredProductsQuantity = await this.countAllUnfilteredProducts() 



      pool = filterDuplicates(pool)

      let previousLength = this.state.limit
      let completed = pool.length

      while (pool.length < this.state.limit) {
        const offset2 = this.state.offset + this.state.limit + this.additive
        let pool2 = await this.takeIds(offset2, this.state.limit - completed, 'completeIds')  
        previousLength = pool2.length

        if (previousLength == 0) {
          break // если длинна до фильтрации = 0 - прекратить процесс
        }
        pool2 = filterDuplicates(pool2)
        
        pool = pool.concat(pool2)   
        pool = filterDuplicates(pool)
        completed = pool.length
        
        this.additive = this.additive + previousLength
      } 
      
      this.setState({
        ids: pool,
        allUnfilteredProductsQuantity: allUnfilteredProductsQuantity
      }, () => {
        this.takeProducts()
      })
    }
    return this.cashIds
  }
  countProductQuantity() {
    if (this.cashIds.length) {
      return (this.cashIds.length - 1) * 50 + this.cashIds[this.cashIds.length - 1].length
    } else {
      
    }
     
  }
  async countAllUnfilteredProducts() { //считаем количество товаров на всех страницах (когда фильтр не применён)
    if (!this.state.allUnfilteredProductsQuantity) {
      return Request('get_ids', {offset: 0})
      .then((res) => {
        res = filterDuplicates(res)

        return Math.ceil(res.length/this.state.limit)
      }, (e) => {
        console.log("ошибка в get_ids")
        this.countAllUnfilteredProducts()
      })
    }
    return this.state.allUnfilteredProductsQuantity
  }
  
  takeData() {
    const page = this.props.page
    if (page === 0) {
      this.additive = 0
    }
    if (this.props.name || this.props.price ||this.props.brand ) {
      this.cashItems = []
      this.beenFiltered = true
    } else if (this.beenFiltered == true) {
      this.cashItems = []
      this.beenFiltered = false
    }

    this.setState({
      offset:  page * this.state.limit,
      ids: [], 
      price: this.props.price,
      brand: this.props.brand,
      name: this.props.name

    }, async () => {
      let result = await this.test()
      if (this.cashIds.length) {

        this.takeProducts()
        if (this.changable) {
          this.props.changeParentValue({productQuantity: this.countProductQuantity(), lastPageQuantity: this.countProductQuantity() })

        }
        this.pagesQuantity = this.cashIds.length
        console.log(this.pagesQuantity)
      }


      /* 
      1) из полученых id вырезаем те, что не подходят по запросу фильтра

      2) добор id (если в пуле не хватает id, то вызвать добор недостающих, в случае успеха внести их во временный пул, в случае неудачи повторить вызов добора)

      3) внести временный пул в state и вызвать взятие товаров по нему(товары вносятся в state уже в функции)
      
      
      
      */
    })

  }
  updateData() {
    this.handleChange(this.props.page, false) 
      .then(() => {
        this.takeData()
      }) 
    

  }
  inputClick(direction) {
    
    if((direction.target.className == 'buttonRight' && this.props.page + 1 < 160 && this.cashIds.length == 0) || (direction.target.className == 'buttonRight' && this.props.page + 1 < this.pagesQuantity)) {
      console.log(this.props.page + 1 )
      this.changable = false
      this.handleChange(this.props.page + 1, false) 
        .then(() => {this.takeData()})    
      
        
      
    } else if(direction.target.className == 'buttonLeft' && this.props.page > 0) {
      console.log(this.props.page - 1 )
      this.changable = false
      this.handleChange(this.props.page - 1, false) 
        .then(() => {this.takeData()}) 

    }
  }

  componentDidMount() {
  }
  componentWillUnmount() { 
    this.takeData()
    
  }
  componentDidUpdate(prevProps) {
    if (((this.props.page !== prevProps.page) && this.props.isLoaded) || 
      this.props.name !== prevProps.name ||
      this.props.price !== prevProps.price ||
      this.props.brand !== prevProps.brand) {
      if (this.props.name !== prevProps.name ||
        this.props.price !== prevProps.price ||
        this.props.brand !== prevProps.brand) {
        this.cashIds = []
        this.changable = true
      }
      this.updateData();
      
    }
  }


  render() {
    let { error, items} = this.state;
    let isLoaded = this.props.isLoaded

    if (error && !error.name == 'Server Error' && !error.name == 'Authorization Error' ) {
      return <div className="">Ошибка {error}</div>
    } else if (!isLoaded ) {
      return (
        <div className="wrapper">
          <button className="buttonLeft" >&lt;</button>
            <div className="container">
              <div className="loading">Загрузка...</div>
            </div>
          <button className="buttonRight">&gt;</button>
        </div>
      );

    } else if (items.length == 0 ) {
      return (
        <div className="wrapper">
          <button className="buttonLeft" >&lt;</button>
            <div className="container">
              <div className="loading">Нет таких товаров</div>
            </div>
          <button className="buttonRight">&gt;</button>
        </div>
      );

    } else {
      return (
        <div className="wrapper">
          <button className="buttonLeft" onClick={this.inputClick}>&lt;</button>
            <div className="container">
              {items.map(item => (
                <Product key={item.id} product={item}/>
              ))}
            </div>
          <button className="buttonRight" onClick={this.inputClick}>&gt;</button>
        </div>
      );    }
  }
}

export default Products
