import React from "react";
import Key from "../Key";
import ServerError from "../ServerError";
import AuthorizationError from "../AuthorizationError";
import Product from "./Product";
import Request from "../Request";

class Products extends React.Component {
  constructor(props) {
    super(props);
 
    this.state = {
      error: null,
      ids: [],
      items: [],
      displayedIds: [],
      limit: 50,
      offset: 0,
    };

    this.inputClick = this.inputClick.bind(this)    
    this.takeData = this.takeData.bind(this)
    this.takeProducts = this.takeProducts.bind(this)
    this.tekeIds = this.tekeIds.bind(this)
    this.updateData = this.updateData.bind(this)

  }
  ids = []
  products = []

  

   tekeIds() {
    Request('get_ids', {offset: this.state.offset, limit: this.state.limit})
      .then((res) => {
        while (res.length < 50) {
          Request('get_ids', {offset: this.state.offset, limit: this.state.limit})
            .then((res2) => {
              res = res.concat(res2)
              console.log(res)
            }, (e) => {
              this.handleError(e, "takeIds")
            })
        }
          this.setState({
            ids: this.state.ids.concat(res.slice(0, 49))
          }, () => {
            this.takeProducts()
            console.log(this.state.ids)
          })
        
      }, (e) => {
        this.handleError(e, "takeIds")
      })
  }

  takeProducts() {
    Request('get_items', {ids: this.state.ids})
      .then((res) => {
        this.setState({
          items: res
        }, () => {
          this.handleChange(this.props.page, true) 
        })
         
      }, (e) => {
        this.handleError(e, "takeProducts")
      })
  }

  handleError(e, request) {
    if (e.name == "Authorization Error" || e.name == "Server Error") {
      this.setState({
        error: null
      }, () => {
        this.handleChange(this.props.page, false)
          .then(() => {
            switch (request) {
              case "takeIds":
                this.tekeIds()
                break;
              case "takeProducts":
                this.takeProducts()
                break;
            
              default: 
                break;
            }
          }) 

      })
    } else {
      this.setState({
        error: e
      }, () => {
        this.handleChange(this.props.page, false)
      })
    }
  }
  async handleChange(page, isLoaded) {
    this.props.changeParentValue(page, isLoaded)
  }

  takeData() {
    let page = this.props.page
    this.setState({
      offset:  page * this.state.limit,
    }, () => {
      this.tekeIds()
      
      
    })

  }
  updateData() {
    this.handleChange(this.props.page, false) 
      .then(() => {
        this.takeData()
      }) 
    

  }
  inputClick(direction) {
    
    if(direction.target.className == 'buttonRight' && this.props.page < 159) {
      console.log(this.props.page + 1 )
      this.handleChange(this.props.page + 1, false) 
        .then(() => {this.takeData()})    
      
        
      
    } else if(direction.target.className == 'buttonLeft' && this.props.page > 0) {
      console.log(this.props.page - 1 )
      this.handleChange(this.props.page - 1, false) 
        .then(() => {this.takeData()}) 

    }
  }

  componentDidMount() {
  }
  componentWillUnmount() { 
    this.takeData()
    
  }
  componentDidUpdate(prevProps) {
    if (this.props.page !== prevProps.page && this.props.isLoaded) {
      this.updateData();
    }
  }


  render() {
    let { error, items} = this.state;
    let isLoaded = this.props.isLoaded

    if (error && !error.name == 'Server Error' && !error.name == 'Authorization Error' ) {
      console.log(error)
      return <div className="">Ошибка {error}</div>
    } else if (!isLoaded ) {
      return (
        <div className="wrapper">
          <button className="buttonLeft" >&lt;</button>
            <div className="container">
              <div className="loading">Загрузка...</div>
            </div>
          <button className="buttonRight">&gt;</button>
        </div>
      );

    } else {
      return (
        <div className="wrapper">
          <button className="buttonLeft" onClick={this.inputClick}>&lt;</button>
            <div className="container">
              {items.map(item => (
                <Product key={item.id} product={item}/>
              ))}
            </div>
          <button className="buttonRight" onClick={this.inputClick}>&gt;</button>
        </div>
      );    }
  }
}

export default Products
