import React from "react"

class NavigationButton extends React.Component {
  constructor(props) {
    super(props) 

    
  }

  id = this.props.id
  pagesQuantity = this.props.pagesQuantity
  page = this.props.page

  countNumbers() {
    if (!this.props.lastPageQuantity) {
      return `${this.id * 50 + 1} - ${this.id * 50 + 50 }`
    } else {
      return `${this.id * 50 + 1} - ${this.props.lastPageQuantity}`
    }
  }

  returnButton(key) {
    if (this.id == this.page) {
      return (
        <button className="current-navigation-button" key={key}>
          {this.countNumbers()}
        </button>
      )
    } else {
      return (
        <button className="navigation-button" key={key}>
          {this.countNumbers()}
        </button>
      )
    }
  }

   

  render() {

    if (this.pagesQuantity > 0) {
      if (this.id == 0 || this.id == this.pagesQuantity - 1 //края всегда отображаются
      || (this.id < 10 && this.page < 5) // первые 11 страниц отображаются пока страница не пройдёт середину
      || (this.id > this.pagesQuantity - 12 && this.page > this.pagesQuantity - 7) // последние 11 страниц отображаются как только страница прошла середину 
      || (this.id >= this.page - 4 && this.id <= this.page + 5)) { // 4 страницы слева и 5 страниц справа от текущей страницы отображаются
      if (this.id == this.page) {
        return (
          <button className="current-navigation-button">
            {this.countNumbers()}
          </button>
        )
      }
      return (
  
        <button className="navigation-button">
          {this.countNumbers()}
        </button>
      )
      } 

    }
    





    

  }
}

export default NavigationButton
