import React from "react";
import Key from "./Key";
import ServerError from "./ServerError";
import AuthorizationError from "./AuthorizationError";
import Product from "./Product";
import Request from "./Request";

class Products extends React.Component {
  constructor(props) {
    super(props);
 
    this.state = {
      error: null,
      isLoaded: false,
      ids: [],
      items: [],
      displayedIds: [],
      limit: 50,
      offset: 0,
    };

    this.inputClick = this.inputClick.bind(this)    
    this.takeData = this.takeData.bind(this)
    this.takeProducts = this.takeProducts.bind(this)
    this.tekeIds = this.tekeIds.bind(this)
    this.updateData = this.updateData.bind(this)

  }
  ids = []
  products = []

  

  tekeIds() {
    Request('get_ids', {offset: this.state.offset, limit: this.state.limit})
      .then((res) => {
        this.setState({
          ids: res
        }, () => {
          this.takeProducts()
        })
      }, (e) => {
        this.handleError(e, "takeIds")
      })
  }

  takeProducts() {
    Request('get_items', {ids: this.state.ids})
      .then((res) => {
        this.setState({
          isLoaded: true,
          items: res
        })
      }, (e) => {
        this.handleError(e, "takeProducts")
      })
  }

  handleError(e, request) {
    if (e.name == "Authorization Error" || e.name == "Server Error") {
      this.setState({
        isLoaded: false,
        error: null
      }, () => {
        switch (request) {
          case "takeIds":
            this.tekeIds()
            break;
          case "takeProducts":
            this.takeProducts()
            break;
        
          default: 
            break;
        }
      })
    } else {
      this.setState({
        isLoaded: false,
        error: e
      })
    }
  }
  handleChange(value) {
    this.props.changePageValue(value)
  }

  takeData() {
    this.setState({
      offset: this.props.page * this.state.limit,
    }, () => {
      //this.makeRequest("https://api.valantis.store:41000/", this.state.offset, this.state.limit)
      this.tekeIds()
      
    })

  }
  updateData() {
    this.setState({ 
      isLoaded: false
    }, () => {        
      this.takeData()
    })
  }
  inputClick(direction) {
    
    if(direction.target.className == 'buttonRight') {
      this.handleChange(this.props.page + 1)
      
      this.setState({ 
        isLoaded: false
      }, () => {        
        this.takeData()
        
      })
        
      
    } else if(direction.target.className == 'buttonLeft' && this.props.page > 0) {
      this.handleChange(this.props.page - 1)
      this.setState({
        isLoaded: false
      }, () => {
        this.takeData()
      })
    }
  }

  componentDidMount() {
  }
  componentWillUnmount() { 
    this.takeData()
    
  }
  componentDidUpdate(prevProps) {
    if (this.props.page !== prevProps.page) {
      this.updateData();
    }
  }


  render() {
    let { error, isLoaded, items} = this.state;


    if (error && !error.name == 'Server Error' && !error.name == 'Authorization Error' ) {
      console.log(error)
      return <div className="">Ошибка {error}</div>
    } else if (!isLoaded ) {
      return (
        <div className="wrapper">
          <button className="buttonLeft" >&lt;</button>
            <div className="container">
              <div className="loading">Загрузка...</div>
            </div>
          <button className="buttonRight">&gt;</button>
        </div>
      );

    } else {
      return (
        <div className="wrapper">
          <button className="buttonLeft" onClick={this.inputClick}>&lt;</button>
            <div className="container">
              {items.map(item => (
                <Product key={item.id} product={item}/>
              ))}
            </div>
          <button className="buttonRight" onClick={this.inputClick}>&gt;</button>
        </div>
      );    }
  }
}

export default Products
