import React from "react"
import NavigationButton from "./NavigationButton"
import Request from "../Request";

class Navigation extends React.Component {
  constructor(props) {
    super(props)

    this.state = {
      error: null, 
      arr: []
    }

    this.handleChange = this.handleChange.bind(this)
    this.changeParentValue = this.changeParentValue.bind(this)
  }
  handleChange(value) {
    this.props.changeParentValue(value, true)
  }
  changeParentValue(value) {
    this.handleChange(value)
  }

  countButtons() {
    Request('get_ids', {offset: 0})
      .then((res) => {
        this.setState({
          arr: this.createArray(res.length/50)
        })
      }, (e) => {
        this.handleError(e)
      })
  }

  handleError(e) {
    if (e.name == "Authorization Error" || e.name == "Server Error") {
      this.setState({
        error: null
      }, () => {
        this.countButtons()
      })
    }
  }
  createArray(length) {
    let arr = []
    for (let i = 0; i < length - 140; i++) {
      arr.push(<NavigationButton key={i} page={i} changeParentValue = {this.changeParentValue} />)
    }
    return arr
  }
  changeCurrent() {
    this.state.arr.map(item => {
      console.log(1)
      if (item.page == this.props.page) {
        item.className = "current-navigation-button"
      }
    })
  }

  componentDidMount() {
  }
  componentWillUnmount() { 
    if (!this.state.arr.length) {
      this.countButtons()
    }
    
    
  }
  componentDidUpdate(prevProps) {
    if (this.props.page !== prevProps.page) {
      this.changeCurrent();
    }
  }



  render() {
    this.changeCurrent();
    let {error, arr} = this.state

    if (error && !error.name == 'Server Error' && !error.name == 'Authorization Error' ) {
      console.log(error)
      return <div className="">Ошибка {error}</div>
    } else {
      return (
      <nav className="navigation-container" >
        {arr}
      </nav>
      )
    }
  }
}

export default Navigation
