import React from "react"

class Input extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
      name: null, 
      price: null, 
      brand: null
    }
  }
  changeState(type, value) {
    switch (type) {
      case "name":
        this.setState({
          name: value
        }, () => {
          this.handleChange()
        })
        break;
      case "price":
        this.setState({
          price: value
        }, () => {
          this.handleChange()
        })
        break;
      case "brand":
        this.setState({
          brand: value
        }, () => {
          this.handleChange()
        })
        break;
    
      default:
        break;
    }
  }
  async handleChange() {
    this.props.changeParentValue({name: this.state.name, price: this.state.price, brand: this.state.brand, page: 0, isLoaded: false})
  }



  render() {
    return (
      <div className="input-container">
        <input className="input-name" type="text" placeholder="Название" onChange={this.changeState("name", this.value)}/>
        <input className="input-price" type="number" placeholder="Цена" onChange={this.changeState("price", this.value)} />
        <input className="input-brand" type="text" placeholder="Бренд" onChange={this.changeState("brand", this.value)}/>
      </div>
    )
  }
}

export default Input
