import React from "react";
import Key from "./Key";

class Ids extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      error: null,
      isLoaded: false,
      ids: [],
      body: {
        action: "get_ids",
        params: {offset: 0, limit: 10}
      }
    };
  }

  componentDidMount() {
    fetch("https://api.valantis.store:41000/",
    { method: 'POST',
      headers: {
        'Content-Type': 'application/json;charset=utf-8',
        'X-Auth': Key()
      },
      body: JSON.stringify(this.body)
    }
    )
      .then(res => res.json()
      )
      .then(
        (res) => {
          
          this.setState({
            isLoaded: true,
            ids: res.result.items
          });
        },
        // Примечание: важно обрабатывать ошибки именно здесь, а не в блоке catch(),
        // чтобы не перехватывать исключения из ошибок в самих компонентах.
        (error) => {
          console.log(this.body)
          this.setState({
            isLoaded: true,
            error
          });
        }
      )
  }

  render() {
    const { error, isLoaded, ids } = this.state;
    if (error) {
      return <div>Ошибка: {error.message}</div>;
    } else if (!isLoaded) {
      return <div>Загрузка...</div>;
    } else {
      return (
        <ul>
          {ids.map(item => (
            <li key={item.id}>
              {item.name} {item.price}
            </li>
          ))}
        </ul>
      );
    }
  }
}

export default Ids
