import React from "react";
import Key from "./Key";

class Products extends React.Component {
  constructor(props) {
    super(props);
 

    this.state = {
      error: null,
      isLoaded: false,

      bodyGetFields: {
        action: "get_fields",
        params: {field: "brand", offset: 3, limit: 5}
      },
      bodyFilter: {
        action: "filter",
        params: {}
      }

    };
  }
  ids = []
  items = []
  body = {
    action: "get_ids",
    params: {offset: 0, limit: 50}
  }
  

  async makeRequest(api, apiBody) {
    fetch(api,
    { method: 'POST',
      headers: {
        'Content-Type': 'application/json;charset=utf-8',
        'X-Auth': Key()
      },
      body: JSON.stringify(apiBody)
    }
    )
      .then(res => res.json())
      .then(
        (res) => {
          switch (apiBody.action) {
            case 'get_ids':
              let tempSet = new Set(res.result)
              this.setState({
                isLoaded: true,
              });
              this.ids = Array.from(tempSet)

              break;
            case 'get_items':
              this.setState({
                isLoaded: true,
              }); 
              this.items = res
              break;          
            default:
              break;
          }
        },
        (error) => {
          
          this.setState({
            isLoaded: true,
            error
          });
          this.makeRequest(api, apiBody)
        }
      )
  }

  takeData() {
    this.makeRequest("https://api.valantis.store:41000/", this.body)
      .then(
        this.body.action = "get_items",
        this.body.params = {
          ids: this.ids
        },
        console.log(this.body)
      )
      .then(
        this.makeRequest("https://api.valantis.store:41000/", this.body)
      )
    
  }
  

  componentDidMount() {
    this.takeData()
  }
  componentWillUnmount() { 
    
   }

  render() {
    
    const { error, isLoaded } = this.state;
    if (error) {
      return <div>Ошибка: {error.message}</div>;
    } else if (!isLoaded) {
      return <div>Загрузка...</div>;
    } else {
      console.log(this.ids)
      console.log(this.items)
      return (
        <div className="">
{/*           {items.map(item => (
            <div key={item.id}>
              <p>
                Бренд: {item.brand}
                id товара: {item.id}
                Цена: {item.price}
                Название: {item.product}
                </p>
            </div>
          ))} */}
        </div>
        
      );    }
  }
}

export default Products
