import React from "react";
import Key from "./Key";

class ServerError extends Error {
  constructor(message) {
    super(message); 
    this.name = "Server Error";
  }

}
class Products extends React.Component {
  constructor(props) {
    super(props);
 
    this.state = {
      error: null,
      isLoaded: false,
      items: [], 
      page: 0,
      displayedIds: [],
      limit: 50,
      offset: 0,
    };

    this.inputClick = this.inputClick.bind(this)    
    this.makeRequest = this.makeRequest.bind(this)
    this.takeData = this.takeData.bind(this)
  }



  

  makeRequest(api, offset, limit) {

      let apiBody = {
        action: "get_ids",
        params: {offset: offset, limit: limit}
      }
      fetch(api,
      { method: 'POST',
        headers: {
          'Content-Type': 'application/json;charset=utf-8',
          'X-Auth': Key()
        },
        body: JSON.stringify(apiBody)
      })
        .then((res) => {
          if(!res.ok) {
            throw new ServerError()
            console.log("error1")
          } else {
            return res
          }
        }, (e) => { throw e; })
        .then((res) => {
          return res.json()
        }, (e) => { throw e; })
        .then((resObj) => {
          let tempSet = new Set(resObj.result)
          let result = Array.from(tempSet)
          return result
          }, (e) => { throw e; })
        .then(
          (uniqueRes) => {
          let apiBodyItems = {
              action: "get_items",
              params: {ids: uniqueRes}
          };
          fetch(api,
            { method: 'POST',
              headers: {
                'Content-Type': 'application/json;charset=utf-8',
                'X-Auth': Key()
              },
              body: JSON.stringify(apiBodyItems)
            })
            .then((res) => {
              if(!res.ok) {
                throw new ServerError()
                console.log("error2")
              } else {
                return res
              }
            }, (e) => { throw e; })
            .then((res) => {
              return res.json();
            }, (e) => { throw e; })
            .then(
              (result) => {
                const tempObj = {};
                const uniqueRes = result.result.filter(({id}) =>(!tempObj[id] && (tempObj[id] = 1)));
  
                this.setState({
                  isLoaded: true,
                  items: uniqueRes
                },() => {
                  return uniqueRes;
                })
          }, 
            (e) => {
              this.setState({
                isLoaded: false,
                error: e
              });
            })
          }, 
          (e) => {
            this.setState({
              isLoaded: false,
              error: e
            });

          })

  }

  takeData() {
    this.setState({
      offset: this.state.page * this.state.limit,
    }, () => {
      this.makeRequest("https://api.valantis.store:41000/", this.state.offset, this.state.limit)
    })

  }
  
  inputClick(direction) {
    if(direction.target.className == 'buttonRight') {
      
      
      this.setState({
        page: this.state.page + 1, 
        isLoaded: false
      }, () => {
        console.log(this.state.page)
        this.takeData()
      })
        
      
    } else if(direction.target.className == 'buttonLeft' && this.state.page > 0) {
      this.setState({
        page: this.state.page - 1, 
        isLoaded: false
      }, () => {
        console.log(this.state.page)
        this.takeData()
      })
    }
  }

  componentWillUnmount() { 
    this.takeData()
  }
  shouldComponentUpdate() {
    this.takeData()
  }

  render() {
    
    const { error, isLoaded, items} = this.state;
    if (error) {
      if (error.name = 'Server Error') {
        console.log(`Ошибка: ${error.name}`)
      } else {
        console.log(`Ошибка: ${error.name}`)
        return <div className="">Ошибка: {error.name}</div>
      }
    } else if (!isLoaded ) {
      return (
        <div className="wrapper">
          <button className="buttonLeft" onClick={this.inputClick}>&lt;</button>
            <div className="container">
              <div className="loading">Загрузка...</div>
            </div>
          <button className="buttonRight" onClick={this.inputClick}>&gt;</button>
        </div>
      );

    } else {
      return (
        <div className="wrapper">
          <button className="buttonLeft" onClick={this.inputClick}>&lt;</button>
            <div className="container">

              {items.map(item => (
                <div className="card" key={item.id}>
                  <h2 className="cardHeader">{item.product}</h2>
                  <p className="brand">Производитель: {(item.brand) ? item.brand : "Не указано"}</p>
                  <p className="price">Цена: {item.price}</p>
                  <p className="id">id: {item.id}</p>
                  <p>{this.state.page}</p>
                </div>
              ))}
            </div>
          <button className="buttonRight" onClick={this.inputClick}>&gt;</button>
        </div>
      );    }
  }
}

export default Products
