import React from "react"

class NavigationButton extends React.Component {
  constructor(props) {
    super(props) 

    
  }
  id = this.props.id
  pagesQuantity = this.props.pagesQuantity
  countData() {
    if (!this.props.lastPageQuantity) {
      return `${this.id * 50 + 1} - ${this.id * 50 + 50 }`
    } else {
      return `${this.id * 50 + 1} - ${this.props.lastPageQuantity}`
    }
  }
  
  

  render() {
    if (this.id == 0 || this.id == this.pagesQuantity - 1 //края всегда отображаются
      || (this.id < 10 && this.props.page < 5) // первые 11 страниц отображаются пока страница не пройдёт середину
      || (this.id > this.pagesQuantity - 12 && this.props.page > this.pagesQuantity - 7) // последние 11 страниц отображаются как только страница прошла середину 
      || (this.id >= this.props.page - 4 && this.id <= this.props.page + 5)) { // 4 страницы слева и 5 страниц справа от текущей страницы отображаются
      if (this.id == this.props.page) {
        return (
          <button className="current-navigation-button">
            {countData()}
          </button>
        )
      }
      return (
  
        <button className="navigation-button">
          {countData()}
        </button>
      )
    } 
    return null
  }
}

export default NavigationButton
