import React from "react";
import Products from "./components/Products";
import Navigation from "./components/Navigation"
import "./css/style.css"

class App extends React.Component {
  constructor(props) {
    super(props)

    this.state = {
      page: 0
    }

    this.changePageValue = this.changePageValue.bind(this)
  }

  changePageValue(value) {
    this.setState({
      page: value
    })
  }

  render() {
    return (
      <main>
          <Navigation changePageValue = {this.changePageValue}/>
          <Products page = {this.state.page} />
 
      </main>
    )
  }
}

export default App
