import React from "react"
import Request from "./Request"
import filterDuplicates from "./FilterDuplicates"

class Input extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
      name: null, 
      price: null, 
      brand: null,
      brandNames: [],
      error: null,
      isLoaded: null
    }
  }
  changeState = (event) => {
    console.log(event.target.value)
    if (event.target.value == 'Бренд') {
      event.target.value = null
    }
    switch (event.target.className) {
      case "input-name":
        if (event.target.value > 0) {
          this.setState({
            name: event.target.value
          }, () => {
            this.handleChange()
          })
        } else {
          this.setState({
            name: null
          }, () => {
            this.handleChange()
          })
        }
        break;
      case "input-price":
        this.setState({
          price: +event.target.value
        }, () => {
          this.handleChange()
        })
        break;
      case "input-brand":
        this.setState({
          brand: event.target.value
        }, () => {
          this.handleChange()
        })
        break;
    
      default:
        break;
    }
  }
  async handleChange() {
    this.props.changeParentValue({name: this.state.name, price: this.state.price, brand: this.state.brand, page: 0, isLoaded: false})
  }
  takeBrands() {
    let body = {
      field: 'brand',
      offset: 0
    }
    return Request('get_fields', body)
      .then((res) => {
        res = filterDuplicates(res)
        res.splice(0, 1)
        this.setState({
          brandNames: res,
          isLoaded: true
        }) 
      }, (e) => {
        console.log("ошибка в get_fields")
        return this.takeBrands()
      })

  } 
  componentDidMount() {
  }
  componentWillUnmount() { 
    this.takeBrands()

    
    
    
  }



  render() {
    const {brandNames, error, isLoaded} = this.state
    if (error && !error.name == 'Server Error' && !error.name == 'Authorization Error' ) {
      console.log(error)
      return <div className="">Ошибка {error}</div>
    } else if (!isLoaded) {
      return <div className="loading">Загрузка...</div>
    } else  {
      return (
        <div className="input-container">
          <input className="input-name" type="text" placeholder="Название" onChange={this.changeState}/>
          <input className="input-price" type="number" placeholder="Цена" onChange={this.changeState} />
          <select className="input-brand" type="text" onChange={this.changeState}>
            <option value="Бренд">Бренд</option>
            {brandNames.map((item) => {
              return <option value={item}>{item}</option>
            })
            }
          </select>
        </div>
      )
    }

  }
}

export default Input
