import React from "react";
import Key from "./Key";

class Card extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      error: null,
      isLoaded: false,
      isLoaded2: false,
      ids: [],
      items: [],
      body: {
        action: "get_ids",
        params: {offset: 0, limit: 50}
      }
    };
  }

  componentDidMount() {
    fetch("https://api.valantis.store:41000/",
    { method: 'POST',
      headers: {
        'Content-Type': 'application/json;charset=utf-8',
        'X-Auth': Key()
      },
      body: JSON.stringify(this.state.body)
    }
    )
      .then(res => res.json())
      .then(
        (res) => {
          
          let tempSet = new Set(res.result)
          
          this.setState({
            isLoaded: true,
            ids: Array.from(tempSet),
            body: {
              action: "get_items",
              params: {ids: Array.from(tempSet)}
            }
          });


          fetch("https://api.valantis.store:41000/",
          { method: 'POST',
            headers: {
              'Content-Type': 'application/json;charset=utf-8',
              'X-Auth': Key()
            },
            body: JSON.stringify(this.state.body)
          }
          )
            .then(res => res.json())
            .then(
              (res) => {
                this.setState({
                  isLoaded2: true,
                  items: res.result
                });
      
      
/*                 fetch("https://api.valantis.store:41000/",
                { method: 'POST',
                  headers: {
                    'Content-Type': 'application/json;charset=utf-8',
                    'X-Auth': Key()
                  },
                  body: JSON.stringify(this.state.body)
                }
                )
                  .then(res => res.json()
                  ) */
              },
      
              (error) => {
                
                this.setState({
                  isLoaded2: true,
                  error
                });
              }
            )

        },

        (error) => {
          
          this.setState({
            isLoaded: true,
            error
          });
        }
      )
  }

  render() {
    const { error, isLoaded2, items } = this.state;
    if (error) {
      return <div>Ошибка: {error.message}</div>;
    } else if (!isLoaded2) {
      return <div>Загрузка...</div>;
    } else {
      return (
        <div className="">
          {items.map(item => (
            <div key={item.id}>
              <p>
                Бренд: {item.brand}
                id товара: {item.id}
                Цена: {item.price}
                Название: {item.product}
                </p>
            </div>
          ))}
        </div>
        
      );    }
  }
}

export default Card
