import React from "react"
import NavigationButton from "./NavigationButton"
import Request from "../Request"
import Ellipses from "../Ellipses"
import filterDuplicates from "../FilterDuplicates"

class Navigation extends React.Component {
  constructor(props) {
    super(props)

    this.state = {
      error: null, 
      arrOfPages: [],
      pagesQuantity: 0, 
      maxLength: 10, 
    }


  }

  countPages(type) {
    switch (type) {
      case 'all':
        Request('get_ids', {offset: 0})
        .then((res) => {
          res = filterDuplicates(res)
          this.setState({
            arrOfPages: this.createArray(res.length/50 - 1),
            pagesQuantity: res.length/50
          })
        }, (e) => {
          this.handleError(e)
        })
        break;
      case 'filtered':
          this.setState({
            pagesQuantity: Math.ceil(this.props.productQuantity / 50)
          })
        break;
    
      default:
        break;
    }

  }

  handleError(e) {
    if (e.name == "Authorization Error" || e.name == "Server Error") {
      this.setState({
        error: null
      }, () => {
        this.countPages()
      })
    }
  }
  createArray(length) {
    let arrOfPages = []
    for (let i = 1; i < length; i++) {
      arrOfPages.push([i])
    }
    return arrOfPages
  }
/*   correctArray(arr) {
    if (arr.length > this.state.maxLength) {
      arr.splice(this.state.maxLength, arr.length - this.state.maxLength)
    } 
    return arr
  } */

  componentDidMount() {
  }
  componentWillUnmount() { 
    if (!this.state.arrOfPages.length) {
      this.countPages('all')
    }
    
    
  }
  componentDidUpdate(prevProps) {
    if (this.props.productQuantity !== prevProps.productQuantity) {
        this.countPages('filtered')
    }
  }



  render() {
    let {error, pagesQuantity, lastPageQuantity, arrOfPages} = this.state


    if (error && !error.name == 'Server Error' && !error.name == 'Authorization Error' ) {
      console.log(error)
      return <div className="">Ошибка {error}</div>
    } else if (pagesQuantity == 1) {
      return (
      <nav className="navigation-container" >
        <NavigationButton key = {0} id = {0} page={this.props.page} pagesQuantity={pagesQuantity} />
      </nav>
      )
    }else {
      return (
      <nav className="navigation-container" >
        <NavigationButton key = {0} id = {0} page={this.props.page} pagesQuantity={pagesQuantity} />
        <Ellipses page={this.props.page} id={0} maxLength={this.state.maxLength} pagesQuantity={pagesQuantity}/>
        {arrOfPages.map(item => (
                <NavigationButton key = {item[0]} id = {item[0]} page={this.props.page} pagesQuantity={pagesQuantity}/>
              ))}
        <Ellipses page={this.props.page} id={1} maxLength={this.state.maxLength} pagesQuantity={pagesQuantity}/>
        <NavigationButton key = {pagesQuantity - 1} id = {pagesQuantity - 1} page={this.props.page} pagesQuantity={pagesQuantity} />
      </nav>
      )
    }
  }
}

export default Navigation
