import React from "react"
import NavigationButton from "./NavigationButton"
import Request from "../Request";

class Navigation extends React.Component {
  constructor(props) {
    super(props)

    this.state = {
      page: 0,
      error: null, 
      length: 0
    }

    this.handleChange = this.handleChange.bind(this)
    this.changeParentValue = this.changeParentValue.bind(this)
  }
  handleChange(value) {
    this.props.changeParentValue(value, true)
  }
  changeParentValue(value) {
    this.handleChange(value)
  }

  countButtons() {
    Request('get_ids', {offset: 0})
      .then((res) => {
        this.setState({
          length: res.length/50
        })
      }, (e) => {
        this.handleError(e)
      })
  }

  handleError(e) {
    if (e.name == "Authorization Error" || e.name == "Server Error") {
      this.setState({
        error: null
      }, () => {
        this.countButtons()
      })
    }
  }

  componentDidMount() {
  }
  componentWillUnmount() { 
    if (this.state.length) {
      this.countButtons()
    }
    
    
  }



  render() {
    let error = this.state.error

    if (error && !error.name == 'Server Error' && !error.name == 'Authorization Error' ) {
      console.log(error)
      return <div className="">Ошибка {error}</div>
    } else {
      return (
      <nav className="navigation-container" >
        <NavigationButton key={0} page={0} changeParentValue = {this.changeParentValue} />
        <NavigationButton key={1} page={1} changeParentValue = {this.changeParentValue}/>
        <NavigationButton key={2} page={2} changeParentValue = {this.changeParentValue}/>
        <NavigationButton key={3} page={3} changeParentValue = {this.changeParentValue}/>
      </nav>
      )
    }
  }
}

export default Navigation
