import React, { useState, useEffect } from "react";
import Key from "./Key";

function MakeRequest(requiredBody) {
  const [error, setError] = useState(null);
  const [isLoaded, setIsLoaded] = useState(false);
  const [ids, setIds] = useState([]);
  const body = requiredBody;
/*   {
    action: "get_ids",
    params: {offset: 0}
  } */

  
  let test2 = fetch("https://api.valantis.store:41000/", {
    method: 'POST',
    headers: {
      'Content-Type': 'application/json;charset=utf-8',
      'X-Auth': Key()
    },
    body: JSON.stringify(body)
  })
    .then(res => res.json())
    .then(
      (res) => {
        setIsLoaded(true);
        let tempSet = new Set(res.result)
        setIds(Array.from(tempSet));
      },
      // Примечание: важно обрабатывать ошибки именно здесь, а не в блоке catch(),
      // чтобы не перехватывать исключения из ошибок в самих компонентах.
      (error) => {
        setIsLoaded(true);
        setError(error);
      }
    )
  

  if (error) {
    console.log(`Ошибка: ${error.message}`);
  } else if (!isLoaded) {
    return <div>Загрузка...</div>;
  } else {
    return ids;
  }
}

function GetItems() {
  const ids = MakeRequest(
    {
      action: "get_ids",
      params: {offset: 0}
    }
  )
  console.log(ids)
/*   const params = MakeRequest(
    {
      action: "get_items",
      params: {"ids": ids.slice(0, 50)}
    }
  )
    console.log(params) */

}


export default GetItems
