import React from "react";
import Key from "./Key";

class Products extends React.Component {
  constructor(props) {
    super(props);
 
    this.state = {
      error: null,
      isLoaded: false,
      items: [], 
      page: 0,
      displayedIds: [],
      limit: 50,
      offset: 0,
    };

    this.inputClick = this.inputClick.bind(this)    
    this.makeRequest = this.makeRequest.bind(this)
    this.takeData = this.takeData.bind(this)
  }



  

  async makeRequest(api, offset, limit) {

      let apiBody = {
        action: "get_ids",
        params: {offset: 0, limit: limit}
      }
      fetch(api,
      { method: 'POST',
        headers: {
          'Content-Type': 'application/json;charset=utf-8',
          'X-Auth': Key()
        },
        body: JSON.stringify(apiBody)
      })
        .then(res => res.json())
        .then(res => {
          let tempSet = new Set(res.result)
          let result = Array.from(tempSet)
          return result
        })
        .then(res => {
          let apiBodyItems = {
              action: "get_items",
              params: {ids: res}
          };
          fetch(api,
            { method: 'POST',
              headers: {
                'Content-Type': 'application/json;charset=utf-8',
                'X-Auth': Key()
              },
              body: JSON.stringify(apiBodyItems)
            })
            .then(res => res.json())
            .then(res => {
              const tempObj = {};
              const result = res.result.filter(({id}) =>(!tempObj[id] && (tempObj[id] = 1)));
              console.log(`Отображено ${result.length} товаров`);

              return result;
              
            })
            .then(res => this.setState({
              isLoaded: true,
              items: res
            }))
          })

  }

  async takeData() {

    this.makeRequest("https://api.valantis.store:41000/", this.state.offset, this.state.limit)


  
  }
  
  inputClick(direction) {
    if(direction.target.className = 'buttonRight') {
      this.setState({page: this.state.page + 1})
      this.componentDidMount()
    } else {
      this.setState({page: this.state.page - 1})
      this.componentDidMount()
    }
  }
  componentDidMount() {
    this.setState({
      offset: this.state.page * this.state.limit,
    })
    console.log(this.state.offset)
    console.log(this.state.items)
    this.takeData()
    
  }
  componentWillUnmount() { 
    
   }

  render() {
    
    const { error, isLoaded, items} = this.state;
    if (error) {
      return <div>Ошибка: {error.message}</div>;
    } else if (!isLoaded) {
      return <div>Загрузка...</div>;
    } else {
      return (
        <div className="wrapper">
          <button className="buttonLeft" onClick={this.inputClick}>&lt;</button>
            <div className="container">

              {items.map(item => (
                <div className="card" key={item.id}>
                  <h2 className="cardHeader">{item.product}</h2>
                  <p className="brand">Производитель: {(item.brand) ? item.brand : "Не указано"}</p>
                  <p className="price">Цена: {item.price}</p>
                  <p className="id">id: {item.id}</p>
                </div>
              ))}
            </div>
          <button className="buttonRight" onClick={this.inputClick}>&gt;</button>
        </div>
      );    }
  }
}

export default Products
