import React from "react"
import NavigationButton from "./NavigationButton"
import Request from "../Request"
import Ellipses from "../Ellipses"
import filterDuplicates from "../FilterDuplicates"

class Navigation extends React.Component {
  constructor(props) {
    super(props)

    this.state = {
      error: null, 
      arrOfPages: [],
      pagesQuantity: 0, 
      maxLength: 10, 
    }


  }

  countPages(type) {
    switch (type) {
      case 'all':
          this.setState({
            pagesQuantity: this.props.allUnfilteredProductsQuantity
          })
        break;
      case 'filtered':
          this.setState({
            arrOfPages: this.createArray(this.props.lastPageQuantity/50 - 1),
            lastPageQuantity: this.props.lastPageQuantity,
            pagesQuantity: Math.ceil(this.props.productQuantity / 50)
          })
        break;
    
      default:
        break;
    }

  }

  handleError(e) {
    if (e.name == "Authorization Error" || e.name == "Server Error") {
      this.setState({
        error: null
      }, () => {
        this.countPages()
      })
    }
  }
  createArray(length) {
    let arrOfPages = []
    for (let i = 1; i < length; i++) {
      arrOfPages.push([i])
    }
    return arrOfPages
  }
/*   correctArray(arr) {
    if (arr.length > this.state.maxLength) {
      arr.splice(this.state.maxLength, arr.length - this.state.maxLength)
    } 
    return arr
  } */

    

  componentDidMount() {
  }
  componentWillUnmount() { 
    if (!this.state.allUnfilteredProductsQuantity) {
      this.countPages('all')
    }
    
    
  }
  componentDidUpdate(prevProps) {
    if ((this.props.productQuantity !== prevProps.productQuantity) || 
    this.props.name !== prevProps.name ||
    this.props.price !== prevProps.price ||
    this.props.brand !== prevProps.brand ||
    this.props.allUnfilteredProductsQuantity !== prevProps.allUnfilteredProductsQuantity) {
      if (this.props.name == null && this.props.price == null && this.props.brand == null) {
        this.countPages('all')

      } else {
        this.countPages('filtered')
      }
      
    }
  }



  render() {
  let pagesQuantity = this.state.pagesQuantity

  if (pagesQuantity == 1) {
      return (
      <nav className="navigation-container" >
        <NavigationButton key = {0} id = {0} lastPageQuantity={pagesQuantity} />
      </nav>
      )
    } else if (pagesQuantity > 0) {
      let array = this.createArray(pagesQuantity)
      return (
      <nav className="navigation-container" >
        <NavigationButton key = {0} id={0} page={this.props.page} />
        <Ellipses page={this.props.page} id={0} maxLength={this.state.maxLength} pagesQuantity={pagesQuantity}/>
        {array.map(item => (
                <NavigationButton page={this.props.page} id={item} pagesQuantity={pagesQuantity}/>
              ))}
        

        <Ellipses page={this.props.page} id={1} maxLength={this.state.maxLength} pagesQuantity={pagesQuantity}/>
        <NavigationButton key = {pagesQuantity - 1} id = {pagesQuantity - 1} page={this.props.page} pagesQuantity={pagesQuantity} lastPageQuantity={pagesQuantity} />
      </nav>
      )
    }
  }
}

export default Navigation
