import React from "react";
import Key from "./Key";
import ServerError from "./ServerError";
import AuthorizationError from "./AuthorizationError";
import Product from "./Product";
import Request from "./Request";

class Products extends React.Component {
  constructor(props) {
    super(props);
 
    this.state = {
      error: null,
      isLoaded: false,
      ids: [],
      items: [], 
      page: 0,
      displayedIds: [],
      limit: 50,
      offset: 0,
    };

    this.inputClick = this.inputClick.bind(this)    
    this.makeRequest = this.makeRequest.bind(this)
    this.takeData = this.takeData.bind(this)
    this.takeProducts = this.takeProducts.bind(this)
    this.tekeIds = this.tekeIds.bind(this)


  }
  ids = []
  products = []


  

  makeRequest(api, offset, limit) {

      let apiBody = {
        action: "get_ids",
        params: {offset: offset, limit: limit}
      }
      fetch(api,
      { method: 'POST',
        headers: {
          'Content-Type': 'application/json;charset=utf-8',
          'X-Auth': Key()
        },
        body: JSON.stringify(apiBody)
      })
        .then((res) => {
          if(!res.ok) {
            switch (res.status) {
              case 500:
                console.log(`Ошибка запроса id товаров. Код: ${res.status}`)
                throw new ServerError
                break;
              case 401:
                console.log(`Ошибка запроса id товаров. Код: ${res.status}`)
                throw new AuthorizationError
                break;
            
              default:
                break;
            }
            
            console.log("error1")
            console.log("res.status")
          } else {
            return res
          }
        }, (e) => { throw e; })
        .then((res) => {
          return res.json()
        }, (e) => { throw e; })
        .then((resObj) => {
          let tempSet = new Set(resObj.result)
          let result = Array.from(tempSet)
          return result
          }, (e) => { throw e; })
        .then(
          (uniqueRes) => {
          let apiBodyItems = {
              action: "get_items",
              params: {ids: uniqueRes}
          };
          fetch(api,
            { method: 'POST',
              headers: {
                'Content-Type': 'application/json;charset=utf-8',
                'X-Auth': Key()
              },
              body: JSON.stringify(apiBodyItems)
            })
            .then((res) => {
              if(!res.ok) {
                switch (res.status) {
                  case 500:
                    console.log(`Ошибка запроса данных товаров. Код: ${res.status}`)
                    throw new ServerError
                    break;
                  case 401:
                    console.log(`Ошибка запроса данных товаров. Код: ${res.status}`)
                    throw new AuthorizationError
                    break;
                
                  default:
                    break;
                }
                
                console.log("error2")
                console.log("res.status")
              } else {
                return res
              }
            }, (e) => { throw e; })
            .then((res) => {
              return res.json();
            }, (e) => { throw e; })
            .then(
              (result) => {
                const tempObj = {};
                const uniqueRes = result.result.filter(({id}) =>(!tempObj[id] && (tempObj[id] = 1)));
  
                this.setState({
                  isLoaded: true,
                  items: uniqueRes
                },() => {
                  return uniqueRes;
                })
          }, 
            (e) => {
              this.setState({
                isLoaded: false,
                error: e
              });
            })
          }, 
          (e) => {
            this.setState({
              isLoaded: false,
              error: e
            });

          })

  }
  tekeIds() {
    Request('get_ids', {offset: this.state.offset, limit: this.state.limit})
      .then((res) => {
        this.takeProducts()
      }, (e) => {
        this.setState({
          isLoaded: false,
          error: e
        });
      })
  }

  takeProducts() {
    Request('get_items', {ids: this.ids})
      .then((res) => {
        this.setState({
          isLoaded: true,
          items: res
        })
      }, (e) => {
        this.setState({
          isLoaded: false,
          error: e
        })
      })

  }


  takeData() {
    this.setState({
      offset: this.state.page * this.state.limit,
    }, () => {
      //this.makeRequest("https://api.valantis.store:41000/", this.state.offset, this.state.limit)

      this.tekeIds()
    })

  }
  
  inputClick(direction) {
    if(direction.target.className == 'buttonRight') {
      
      
      this.setState({
        page: this.state.page + 1, 
        isLoaded: false
      }, () => {
        console.log(this.state.page)
        
        this.takeData()
        
      })
        
      
    } else if(direction.target.className == 'buttonLeft' && this.state.page > 0) {
      this.setState({
        page: this.state.page - 1, 
        isLoaded: false
      }, () => {
        console.log(this.state.page)
        this.takeData()
      })
    }
  }

  componentDidMount() {
  }
  componentWillUnmount() { 
    this.takeData()
    
  }
  


  render() {

    let { error, isLoaded, items} = this.state;
    if (error) {
      if (error.name == "Server Error") {
        this.state.error = null
        this.takeData()
      }
    } else if (!isLoaded ) {
      return (
        <div className="wrapper">
          <button className="buttonLeft" onClick={this.inputClick}>&lt;</button>
            <div className="container">
              <div className="loading">Загрузка...</div>
            </div>
          <button className="buttonRight" onClick={this.inputClick}>&gt;</button>
        </div>
      );

    } else {
      return (
        <div className="wrapper">
          <button className="buttonLeft" onClick={this.inputClick}>&lt;</button>
            <div className="container">

              {items.map(item => (
                <Product key={item.id} product={item}/>
              ))}
            </div>
          <button className="buttonRight" onClick={this.inputClick}>&gt;</button>
        </div>
      );    }
  }
}

export default Products
