import React from "react"
import Products from "./components/Products/Products"
import Navigation from "./components/Navigation/Navigation"
import Input from "./components/Input"
import "./css/style.css"

class App extends React.Component {
  constructor(props) {
    super(props)

    this.state = {
      page: 0, 
      isLoaded: false,
      name: null,
      price: null, 
      brand: null, 
      productQuantity: null,
      lastPageQuantity: null,
      lastPageQuantityFiltered: null,

      allUnfilteredProductsQuantity: 0, 
      filterEnabled: false,
    }

    this.changeParentValue = this.changeParentValue.bind(this)
  }

  changeParentValue(data) {
    this.setState(data,() => {
      console.log(this.state.productQuantity)
    })
  }

  render() {
    return (
      <main>
          <Input changeParentValue = {this.changeParentValue} />
          <Navigation changeParentValue = {this.changeParentValue} page = {this.state.page} productQuantity={this.state.productQuantity} allUnfilteredProductsQuantity={this.state.allUnfilteredProductsQuantity} isLoaded = {this.state.isLoaded} name = {this.state.name} price = {this.state.price} brand = {this.state.brand} lastPageQuantity={this.state.lastPageQuantity} lastPageQuantityFiltered={this.statelastPageQuantityFiltered} />
          <Products page = {this.state.page} isLoaded = {this.state.isLoaded} changeParentValue = {this.changeParentValue} name = {this.state.name} price = {this.state.price} brand = {this.state.brand} filterEnabled={this.state.filterEnabled}/>
          <Navigation changeParentValue = {this.changeParentValue} page = {this.state.page} productQuantity={this.state.productQuantity} allUnfilteredProductsQuantity={this.state.allUnfilteredProductsQuantity} isLoaded = {this.state.isLoaded} name = {this.state.name} price = {this.state.price} brand = {this.state.brand} lastPageQuantity={this.state.lastPageQuantity} lastPageQuantityFiltered={this.statelastPageQuantityFiltered}/>
      </main>
    )
  }
}

export default App
