import React from "react";
import Key from "./Key";

class Products extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      error: null,
      isLoaded: false,
      isLoaded2: false,
      items: [],
      bodyGetFields: {
        action: "get_fields",
        params: {field: "brand", offset: 3, limit: 5}
      },
      bodyFilter: {
        action: "filter",
        params: {}
      }

    };
  }

  async makeRequest(api, apiBody) {
    fetch(api,
    { method: 'POST',
      headers: {
        'Content-Type': 'application/json;charset=utf-8',
        'X-Auth': Key()
      },
      body: JSON.stringify(apiBody)
    }
    )
      .then(res => res.json())
      .then(
        (res) => {
          switch (apiBody.action) {
            case 'get_ids':
              let tempSet = new Set(res.result)
              let result = Array.from(tempSet)
              this.setState({
                isLoaded: true
              });
              this.state.items = result

              console.log(result) 
              console.log(this.state.bodyGetItems) 
              break;
            case 'get_items':
              this.setState({
                isLoaded: true,
                items: res.result
              }); 
              break;          
            default:
              break;
          }
        },
        (error) => {
          
          this.setState({
            isLoaded: true,
            error
          });
          this.makeRequest(api, apiBody)
        }
      )
  }

  takeData() {
    this.makeRequest("https://api.valantis.store:41000/", {
      action: "get_ids",
      params: {offset: 0, limit: 50}
    })
      .then(
        this.makeRequest("https://api.valantis.store:41000/", {
          action: "get_items",
          params: {ids: this.state.items}
        })
      )
    
  }
  

  componentDidMount() {
    this.takeData()
  }
  componentWillUnmount() { 
    
   }

  render() {
    
    const { error, isLoaded, items } = this.state;
    if (error) {
      return <div>Ошибка: {error.message}</div>;
    } else if (!isLoaded) {
      return <div>Загрузка...</div>;
    } else {
      /* console.log(items) */
      return (
        <div className="">
{/*           {items.map(item => (
            <div key={item.id}>
              <p>
                Бренд: {item.brand}
                id товара: {item.id}
                Цена: {item.price}
                Название: {item.product}
                </p>
            </div>
          ))} */}
        </div>
        
      );    }
  }
}

export default Products
